mapper.select:
SELECT * FROM( SELECT ROWNUM AS ROW_NUM, alias.* FROM( SELECT a.article_id, a.m_id, a.article_title, a.article_subtitle, a.article_view, a.article_like, a.article_share, a.article_reply, a.pic_id, a.category_id, p.pic_url, a.article_date, m.m_nickname, m.m_pic FROM ARTICLE a JOIN PROFILE m ON a.m_id=m.m_id JOIN PICTURE p ON a.pic_id=p.pic_id WHERE
a.article_title LIKE #{item} OR a.article_subtitle LIKE #{item}
ORDER BY a.article_view DESC )alias )WHERE ROW_NUM BETWEEN #{num}+1 and #{num}+20

	SELECT * FROM(
		SELECT ROWNUM AS ROW_NUM, alias.*
		FROM(
			SELECT profile_id, m_id, m_nickname, m_pic, m_introduce FROM PROFILE WHERE
				
	      			m_nickname LIKE #{item} OR m_introduce LIKE #{item}
	     		
	    )alias
     )WHERE ROW_NUM BETWEEN #{num}+1 and #{num}+20
